from fastapi import FastAPIfrom routers import usersapp = FastAPI()# APIrouter를 활용하여 리팩토링 ------------------------ 2026.01.20# 라우터 등록app.include_router(users.router)# 정상 동작 여부 확인@app.get("/")def health_check():    return {        "status": "ok",        "message": "FastAPI 서버가 정상 동작중입니다. "    }